# s = "abcdefghijklmnopqrstuvwxyz"
s = ""


def solve(n, a, b):
    ans = ''
    ans += s[:b]
    base = s[:b]
    if len(ans) < a:
        while len(ans) < n:
            ans += base
    while len(ans) < n:
        ans += s[b:n+1]
    print(ans[:n])


for i in range(int(input())):
    n, a, b = map(int, input().split())
    solve(n, a, b)
